fn squareHoleSketch = () => {
  const squareSketch = startSketchOn('-XZ')
    |> startProfileAt([-1, -1], %)
    |> line([2, 0], %)
    |> line([0, 2], %)
    |> line([-2, 0], %)
    |> close(%)
  return squareSketch
}

